import React, {useState, useEffect} from "react";
import { Pagination, Typography, Input } from "antd";
import CommentList from "./CommentList"

interface comment {
    postId: number,
    id: number,
    name: string,
    email: string,
    body: string

}


const App: React.FC = () =>{
    const {Text} = Typography;
    const PAGE_SIZE = 10
    const { Search } = Input;
    const [comments, setComments] = useState<comment[]>([])
    const [elem, setElem] = useState<JSX.Element>()
    const [currentPage, setcurrentPage]= useState(1)
    const [email, setEmail] = useState('')
    useEffect(()=>{
    const comments = 'https://jsonplaceholder.typicode.com/comments'
    console.log(email);

    const url = email === '' ? comments : comments+`?email=${email}`
    console.log(url);
    fetch(url)
    .then(response => response.json())
    .then(json => setComments(json))

    },[email])
    useEffect(()=>{
        const start = (currentPage - 1) * PAGE_SIZE
        const end = start + PAGE_SIZE
        const com = comments.slice(start, end)

        const list = <Typography style={{display: 'flex', flexDirection: 'column'}}>{com.map((elem)=>{ 
            return (
            <>
            <Text strong>{elem.email}</Text>
            <Text>{elem.body}</Text>
            </>
            )
          })}</Typography>
        setElem(list)

    },[comments, currentPage])
    return <>
        <Search 
            placeholder="Поиск по email" 
            onSearch={(value)=>setEmail(value)} 
            allowClear 
            enterButton 
        />
        <Pagination 
            simple 
            pageSize={PAGE_SIZE} 
            current={currentPage} 
            total={comments.length} 
            onChange={(page) => setcurrentPage(page)}
        />
        {elem}
        <CommentList comments={comments} currentPage={currentPage} pageSize={PAGE_SIZE} />
    </>
}

export default App;
