import React, {useState, useEffect} from "react";
import { Pagination, Typography, Input } from "antd";
import {getComments} from "./request"


interface comment {
    postId: number,
    id: number,
    name: string,
    email: string,
    body: string
}

const App: React.FC = () =>{
    const PAGE_SIZE = 10
    const { Title, Paragraph, Text, Link } = Typography;
    const { Search } = Input;
    const [comments, setComments] = useState<comment[]>([])
    const [elem, setElem] = useState<JSX.Element>()
    const [currentPage, setcurrentPage]= useState(1)
    const [email, setEmail] = useState<string>('Eliseo@gardner.biz')
    // const test = getComments().then(response => response)
    // const respons = fetch("https://jsonplaceholder.typicode.com/comments").then(async (respons)=>setComments(await respons.json()))
    // const respons = await fetch("https://jsonplaceholder.typicode.com/comments")
    useEffect(()=>{
    fetch(`https://jsonplaceholder.typicode.com/comments?email=${email}`)
    .then(response => response.json())
    .then(json => setComments(json))

    },[email])
    useEffect(()=>{
        const start = (currentPage - 1) * PAGE_SIZE
        const end = start + PAGE_SIZE
        const com = comments.slice(start, end)

        const list = <Typography style={{display: 'flex', flexDirection: 'column'}}>{com.map((elem)=>{ 
            return (
            <>
            <Text strong>{elem.email}</Text>
            <Text>{elem.body}</Text>
            </>
            )
          })}</Typography>
        setElem(list)

    },[comments, currentPage])
    const onSearch = (value: string) => setEmail(value);
    return <><Search placeholder="Поиск по email" onSearch={onSearch} allowClear enterButton /><Pagination simple pageSize={PAGE_SIZE} current={currentPage} total={comments.length} onChange={(page) => setcurrentPage(page)}/>{elem}</>
}

