    const lastPage = Math.ceil(comments.length / PAGE_SIZE);
    },[])
    useEffect(()=>{
        const list = <Typography style={{display: 'flex', flexDirection: 'column'}}>{comments.map((elem)=>{ 
            return (
            <>
            <Text strong>{elem.email}</Text>
            <Text>{elem.body}</Text>
            </>
            )
          })}</Typography>
        setElem(list)

    },[comments])
    return <><Pagination simple pageSize={PAGE_SIZE} current={currentPage} total={comments.length} onChange={(page) => setcurrentPage(page)}/>{elem}</>
}

