import React, {useState, useEffect} from "react";
import { Pagination, Typography } from "antd";
import {getComments} from "./request"


interface comment {
    postId: number,
    id: number,
    name: string,
    email: string,
    body: string
}

const App: React.FC = () =>{
    const { Title, Paragraph, Text, Link } = Typography;
    const [comments, setComments] = useState<comment[]>([])
    const [elem, setElem] = useState<JSX.Element>()
    // const test = getComments().then(response => response)
    // const respons = fetch("https://jsonplaceholder.typicode.com/comments").then(async (respons)=>setComments(await respons.json()))
    // const respons = await fetch("https://jsonplaceholder.typicode.com/comments")
    useEffect(()=>{
    fetch('https://jsonplaceholder.typicode.com/comments')
    .then(response => response.json())
    .then(json => setComments(json))
    },[])
    useEffect(()=>{
        const list = <Typography style={{display: 'flex', flexDirection: 'column'}}>{comments.map((elem)=>{ return <Text strong>
            {elem.email}
          </Text>})}</Typography>
        setElem(list)

    },[comments])
    return <><Pagination simple pageSize={2} defaultCurrent={1} total={comments.length} />{elem}</>
}

